styles.text:
@keyframes mat-checkbox-unchecked-indeterminate-mixedmark { 
  0%, 68.2% { transform: scaleX(0); }
  68.2% { animation-timing-function: cubic-bezier(0, 0, 0, 1); }
  100% { transform: scaleX(1); }
}

@keyframes mat-checkbox-checked-indeterminate-checkmark { 
  0% { animation-timing-function: cubic-bezier(0, 0, 0.2, 0.1); opacity: 1; transform: rotate(0deg); }
  100% { opacity: 0; transform: rotate(45deg); }
}

@keyframes mat-checkbox-indeterminate-checked-checkmark { 
  0% { animation-timing-function: cubic-bezier(0.14, 0, 0, 1); opacity: 0; transform: rotate(45deg); }
  100% { opacity: 1; transform: rotate(360deg); }
}

@keyframes mat-checkbox-checked-indeterminate-mixedmark { 
  0% { animation-timing-function: cubic-bezier(0, 0, 0.2, 0.1); opacity: 0; transform: rotate(-45deg); }
  100% { opacity: 1; transform: rotate(0deg); }
}

@keyframes mat-checkbox-indeterminate-checked-mixedmark { 
  0% { animation-timing-function: cubic-bezier(0.14, 0, 0, 1); opacity: 1; transform: rotate(0deg); }
  100% { opacity: 0; transform: rotate(315deg); }
}

@keyframes mat-checkbox-indeterminate-unchecked-mixedmark { 
  0% { animation-timing-function: linear; opacity: 1; transform: scaleX(1); }
  100%, 32.8% { opacity: 0; transform: scaleX(0); }
}
